# icons.py
# This file stores the SVG data for the application icons.

from PySide6.QtGui import QIcon, QPixmap
from PySide6.QtCore import QByteArray

# A helper function to create a QIcon from an SVG string and apply a color.
def create_icon_from_svg(svg_string: str, color: str) -> QIcon:
    """Creates a QIcon from a raw SVG string, applying a fill or stroke color."""
    # This will replace "currentColor" whether it's used for fill or stroke.
    colored_svg = svg_string.replace('currentColor', color)
    
    svg_bytes = colored_svg.encode('utf-8')
    q_byte_array = QByteArray(svg_bytes)
    
    pixmap = QPixmap()
    pixmap.loadFromData(q_byte_array, 'svg')
    
    return QIcon(pixmap)

